package com.example.project14.Match;

import android.content.ClipData;
import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.bumptech.glide.load.resource.bitmap.CircleCrop;
import com.bumptech.glide.request.RequestOptions;
import com.example.project14.R;

import java.util.ArrayList;
import java.util.List;

public class MatchAdapter extends RecyclerView.Adapter<MatchAdapter.MatchViewHolder> {

    private LayoutInflater layoutInflater;
    private List<Match> matches;

    public MatchAdapter(Context context) {
        this.layoutInflater = LayoutInflater.from(context);
        this.matches = createDummyData();
    }


    @NonNull
    @Override
    public MatchViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View mItemView = layoutInflater.inflate(R.layout.item_match,
                parent, false);

        return new MatchViewHolder(mItemView);


    }


    @Override
    public void onBindViewHolder(@NonNull MatchViewHolder holder, int position) {
        Match match = matches.get(position);


        String[] split = match.getName().split(" ");
        String firstName = split[0];
        String userName = firstName.substring(0, Math.min(firstName.length(), 8));
        holder.username.setText(userName);


        holder.age.setText(String.valueOf(" (" + match.getAge() + ")"));
        holder.city.setText(match.getCity());
        Glide.with(holder.profileImage)
                .load(match.getImageURL())
                .centerCrop()
                .apply(RequestOptions.bitmapTransform(new CircleCrop()))
                .into(holder.profileImage);
    }

    @Override
    public int getItemCount() {
        return matches.size();
    }


    private List<Match> createDummyData() {
        List<Match> dummyData = new ArrayList<>();

        // Create and add dummy Chat objects to the list
        dummyData.add(new Match("Rudy J", 91, "Breda", "data:image/png;base64,"));
        dummyData.add(new Match("Willem Nijkamp", 89, "Amsterdam", "data:image/jpeg;base64,"));
        dummyData.add(new Match("Bep", 72, "Breda", "data:image/jpeg;base64,"));
        dummyData.add(new Match("Nikita Oriwamani", 91, "Breda", "data:image/png;base64,"));
        dummyData.add(new Match("User 5", 89, "Breda", "data:image/jpeg;base64,"));
        dummyData.add(new Match("User 6", 72, "Amsterdam", "data:image/jpeg;base64,"));
        dummyData.add(new Match("User 7", 91, "Breda", "data:image/png;base64,"));
        dummyData.add(new Match("User 8", 89, "Amsterdam", "data:image/jpeg;base64,"));
        dummyData.add(new Match("User 9", 72, "New York", "data:image/jpeg;base64,"));
        dummyData.add(new Match("User 10", 91, "Amsterdam", "data:image/png;base64,"));
        dummyData.add(new Match("User 11", 89, "New York", "data:image/jpeg;base64,"));
        dummyData.add(new Match("User 12", 72, "Breda", "data:image/jpeg;base64,"));

        // Add more dummy data as needed

        return dummyData;
    }

    class MatchViewHolder extends RecyclerView.ViewHolder {
        public TextView username;
        public TextView age;
        public ImageView profileImage;

        public TextView city;

        public MatchViewHolder(@NonNull View ItemView) {
            super(ItemView);
            this.username = ItemView.findViewById(R.id.match_username);
            this.age = ItemView.findViewById(R.id.match_age);
            this.city = ItemView.findViewById(R.id.match_city);
            this.profileImage = ItemView.findViewById(R.id.match_pfp);
        }

    }
}
